    /**
     * @test
     */
    public function prepare() {
        $s = ':id=?';
        $args = array('id' => 10);

        $prepared = $this->query->prepare($s, $args);
        $this->assertEquals(
            "\"id\"='10'",
            $prepared
        );

        $s = 'where :a = ? and :b =?';
        $args = array('a' => 1, 'b' => "I'm rock");

        $prepared = $this->query->prepare($s, $args);
        $this->assertEquals(
            "where \"a\" = '1' and \"b\" ='I''m rock'",
            $prepared
        );


        $s = ':a > ? or :a = :name and :name !=? and :c <> abs(:d) or :dd or :d';
        $args = array('a' => 1, 'name' => "I'm rock");

        $prepared = $this->query->prepare($s, $args);
        $this->assertEquals(
            "\"a\" > '1' or \"a\" = \"name\" and \"name\" <>'I''m rock' and \"c\" <> abs(\"d\") or \"dd\" or \"d\"",
            $prepared
        );


        $s = 'select :id, :name, :birthday from :profile where :age >= ?;';
        $args = array('age' => '20');

        $prepared = $this->query->prepare($s, $args);
        $this->assertEquals(
            "select \"id\", \"name\", \"birthday\" from \"profile\" where \"age\" >= '20';",
            $prepared
        );
    }

    /**
     * @test
     */
    public function prepare_without_colon() {
        $s = 'id=?';
        $args = array('id' => 10);

        $prepared = $this->query->prepare($s, $args);
        $this->assertEquals(
            "\"id\"='10'",
            $prepared
        );

        $s = 'where a = ? and b =?';
        $args = array('a' => 1, 'b' => "I'm rock");

        $prepared = $this->query->prepare($s, $args);
        $this->assertEquals(
            "where \"a\" = '1' and \"b\" ='I''m rock'",
            $prepared
        );


        $s = 'a > ? or a = name and name !=? and :c <> abs(:d) or :dd or :d';
        $args = array('a' => 1, 'name' => "I'm rock");

        $prepared = $this->query->prepare($s, $args);
        $this->assertEquals(
            "\"a\" > '1' or \"a\" = \"name\" and \"name\" <>'I''m rock' and \"c\" <> abs(\"d\") or \"dd\" or \"d\"",
            $prepared
        );

        // Because fileds c, d, dd are not in $args. Prepare will not think they as fields.
        $s = 'a > ? or a = name and name !=? and c <> abs(d) or dd or d';
        $args = array('a' => 1, 'name' => "I'm rock");

        $prepared = $this->query->prepare($s, $args);
        $this->assertNotEquals(
            "\"a\" > '1' or \"a\" = \"name\" and \"name\" <>'I''m rock' and \"c\" <> abs(\"d\") or \"dd\" or \"d\"",
            $prepared
        );

        $s = 'select * from profile where age >= ? and height > ? and weight < ? and class != ?;';
        $args = array('profile' => 'this is table', 'age' => 20, 'height' => 170, 'weight' => 80, 'class' => 'L' );

        $prepared = $this->query->prepare($s, $args);
        $this->assertEquals(
            "select * from \"profile\" where \"age\" >= '20' and \"height\" > '170' and \"weight\" < '80' and \"class\" <> 'L';",
            $prepared
        );
    }

